            $(function()
            {
             // Sign-in Modal Handlers
             
             $("#signInForm").bootstrapValidator(
             {
              live: "submitted"
             });
             
             $(document).on("success.form.bv", "#signInForm", function(e)
             {
              var bv = $('#signInForm').data("bootstrapValidator");
              
              if (bv && bv.validate && bv.isValid)
              {
               if (bv.isValid())
                isEmailExist();
              }
              
              return false;
             });
             
             $(document).on("click", "button.sign-in", function(e)
             {
              $("#signInForm").bootstrapValidator("validate");
              
              return false;
             });
             
             $(document).on("shown.bs.modal", "#myModal", function(e)
             {
              $(this).find("input[name='emailAddress']").focus();
             });
             
             // Prior Applicant Modal Handlers
             
             $("#priorApplForm").bootstrapValidator(
             {
              live: "submitted"
             });
             
             $(document).on("success.form.bv", "#priorApplForm", function(e)
             {
              e.preventDefault();
              
              var bv = $('#priorApplForm').data("bootstrapValidator");
              
              if (bv && bv.validate && bv.isValid)
              {
               if (bv.isValid())
                isStudentExist();
              }
              
              return false;
