            function isStudentExist()
            {
                var e = $('[name=readmitemailAddress]').val();
                var p = $('[name=bannerId]').val();
                
                $('[name=bannerId]').val('');
                
                $('.loading-signIN').show();
                
                Visualforce.remoting.Manager.invokeAction(
                    'FastAppVFC.IsStudentExist',
                    e, p, "Med",
                    function(result, event)
                    {
                        if (event.status)
                        {
                            if (result && result.AccountId)
                            {
                                dataLayer.push(
                                {
                                    'event': 'e_accountLogin',
                                    'userId': result.EncryptedEmail,
                                    'loginStatusSession': true
                                });

                                window.location.href = 'https://rossu.secure.force.com/apply/listAllApps?student=' + result.AccountId +'&readmit=true&appType=Med';
                            }
                            else
                            {
                                $('.student-sign-in-error').css('display', 'block');
                                $('.loading-signIN').hide();
                            }
                        }
                        else
                        {
                            alert("Invalid Username or Password");
                        }
                    },
                    {
                        escape: true
                    }
                );
            }
            
            function isEmailExist()
            {
                var e = $('[name=emailAddress]').val();
                var p = $('[name=password]').val();
                
                $('[name=password]').val('');
                
                $('.loading-signIN').show();
                
                Visualforce.remoting.Manager.invokeAction(
                    'FastAppVFC.IsEmailExist',
                    e, p, "Med",
                    function(result, event)
                    {
                        if (event.status)
                        {
                            if (result)
                            {                                
                                if (result.AccountId)
                                {
                                    window.location.href = 'https://rossu.secure.force.com/apply/listAllApps?student=' + result.AccountId +'&readmit=false&appType=Med';
                                }
                                else
                                {
                                    $('[id$=signInId]').val(result.ApplicationId);
                                    
                                    redirectPage();
                                }

                                dataLayer.push(
                                {
                                    'event': 'e_accountLogin',
                                    'userId': result.EncryptedEmail,
                                    'loginStatusSession': true
                                });
                            }
                            else
                            {
                                $('.sign-in-error').css('display', 'block');
                                $('.loading-signIN').hide();
                            }
                        }
                    },
                    {
                        escape: true
                    }
                );
            }            
